import{_ as e,o as i,c as o,a as t}from"./index-0583e027.js";const n="/assets/autorica-5e5f30e9.png";const s={name:"About"},j={class:"about-container"};function d(r,a,u,p,c,v){return i(),o("div",j,a[0]||(a[0]=[t('<div class="text-box" data-v-a40d873d><h1 class="about-title" data-v-a40d873d>Autorica</h1><p class="about-text" data-v-a40d873d> Pedagoginja po struci, Vinkovčanka Kristina Kulić zna koliko je važno djecu poticati na učenje od najranije dobi. Izazov u odgajanju i poučavanju svoja dva dječaka istih razvojnih potreba potaknuo ju je na razvijanje interaktivne mape kako bi ih zainteresirala i zabavila. Potez se pokazao kao pun pogodak pa se njezina privatna zbirka vrlo brzo proširila i na drugu djecu. Njezina Knjižuljica oduševljava i male i velike, a najsretnija je, kaže, što ispunjava svoju svrhu – djeca ju vole, zabavna im je i uz nju uče. Interaktivna mapa za poticanje ranog učenja donosi aktivnosti kakve se mogu pronaći u Montessori pedagogiji, a uključuje sličice koje se lijepe čičak točkama i piši-briši zadatci. </p><p class="about-text" data-v-a40d873d> „Izrađujem i kartice za učenje namijenjene za učenje pojmova, razvoj i poticanje govora te za poticanje učenja djece s posebnim potrebama. Podijeljene su po kategorijama, npr. životinje, voće i povrće, osjećaji, zanimanja, boje, itd“, priča nam ova 35-godišnjakinja, magistrica pedagogije i edukacije filozofije, trenutno zaposlena u osnovnoj školi kao stručna suradnica pedagoginja. </p></div><div class="image-container" data-v-a40d873d><img src="'+n+'" alt="Slika autorice" class="author-image" data-v-a40d873d></div>',2)]))}const k=e(s,[["render",d],["__scopeId","data-v-a40d873d"]]);export{k as default};
